
// generated by ChatGPT
const categories = {
  blending: [
    "human hand",
    "clouds",
    "metal spoon",
    "flower petals",
    "draped cloth",
    "sphere under soft light",
  ],
  hatching: [
    "feather",
    "fish scales",
    "old book",
    "brick wall",
    "folded paper",
    "roof tiles",
  ],
  stippling: [
    "leopard",
    "tree trunk",
    "coral",
    "cactus",
    "shell",
    "gravel path",
  ],
}



function prompt(category) {
  if (categories[category]) {
    const words = categories[category];
    return words[Math.floor(Math.random() * words.length)];
  }
  return null; 
}


module.exports = { prompt };
